      return;
    }

    // Convert to inventory items and enrich with market data
    const inventoryItems: InsertInventoryItem[] = [];

    await ctx.reply(`üîÑ –û–±–æ–≥–∞—â–∞—é –¥–∞–Ω–Ω—ã–µ –∏–∑ Warframe Market...`);

    for (const item of excelItems) {
