import { users, sessions, inventoryItems, type User, type InsertUser, type Session, type InsertSession, type InventoryItem, type InsertInventoryItem } from "@shared/schema";
import { db } from "./db";
import { eq, and, lt } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByTelegramId(telegramId: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Session operations
  getSession(id: string): Promise<Session | undefined>;
  getActiveSessionByTelegramId(telegramId: string): Promise<Session | undefined>;
  createSession(session: InsertSession): Promise<Session>;
  updateSessionStatus(id: string, status: "active" | "completed" | "cancelled"): Promise<void>;
  getExpiredSessions(): Promise<Session[]>;

  // Inventory item operations
  getItemsBySessionId(sessionId: string): Promise<InventoryItem[]>;
  createInventoryItem(item: InsertInventoryItem): Promise<InventoryItem>;
  createInventoryItems(items: InsertInventoryItem[]): Promise<InventoryItem[]>;
  deleteItemsBySessionId(sessionId: string): Promise<void>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByTelegramId(telegramId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.telegramId, telegramId));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getSession(id: string): Promise<Session | undefined> {
    const [session] = await db.select().from(sessions).where(eq(sessions.id, id));
    return session || undefined;
  }

  async getActiveSessionByTelegramId(telegramId: string): Promise<Session | undefined> {
    const [session] = await db
      .select()
      .from(sessions)
      .where(and(
        eq(sessions.telegramId, telegramId),
        eq(sessions.status, "active")
      ));
    return session || undefined;
  }

  async createSession(insertSession: InsertSession): Promise<Session> {
    const [session] = await db
      .insert(sessions)
      .values(insertSession)
      .returning();
    return session;
  }

  async updateSessionStatus(id: string, status: "active" | "completed" | "cancelled"): Promise<void> {
    await db
      .update(sessions)
      .set({ 
        status, 
        completedAt: status === "completed" ? new Date() : null 
      })
      .where(eq(sessions.id, id));
  }

  async getExpiredSessions(): Promise<Session[]> {
    const now = new Date();
    return await db
      .select()
      .from(sessions)
      .where(and(
        eq(sessions.status, "active"),
        lt(sessions.expiresAt, now)
      ));
  }

  async getItemsBySessionId(sessionId: string): Promise<InventoryItem[]> {
    return await db
      .select()
      .from(inventoryItems)
      .where(eq(inventoryItems.sessionId, sessionId));
  }

  async createInventoryItem(insertItem: InsertInventoryItem): Promise<InventoryItem> {
    const [item] = await db
      .insert(inventoryItems)
      .values([insertItem])
      .returning();
    return item;
  }

  async createInventoryItems(insertItems: InsertInventoryItem[]): Promise<InventoryItem[]> {
    if (insertItems.length === 0) return [];

    return await db
      .insert(inventoryItems)
      .values(insertItems)
      .returning();
  }

  async deleteItemsBySessionId(sessionId: string): Promise<void> {
    await db
      .delete(inventoryItems)
      .where(eq(inventoryItems.sessionId, sessionId));
  }
}

export const storage = new DatabaseStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, jsonb, timestamp, boolean, check } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { relations } from "drizzle-orm";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  telegramId: text("telegram_id").notNull().unique(),
  username: text("username"),
  firstName: text("first_name"),
  lastName: text("last_name"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const sessions = pgTable("sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").references(() => users.id, { onDelete: "cascade" }).notNull(),
  telegramId: text("telegram_id").notNull(),
  type: text("type", { enum: ["multishot", "edit", "price_update", "split_excel"] }).notNull(),
  status: text("status", { enum: ["active", "completed", "cancelled"] }).default("active").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  completedAt: timestamp("completed_at"),
  photoLimit: integer("photo_limit").notNull().default(16),
  expiresAt: timestamp("expires_at"),
}, (table) => ({
  typeCheck: check("type_check", sql`${table.type} IN ('multishot', 'edit', 'price_update', 'split_excel')`),
  statusCheck: check("status_check", sql`${table.status} IN ('active', 'completed', 'cancelled')`),
}));

export const inventoryItems = pgTable("inventory_items", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: varchar("session_id").references(() => sessions.id, { onDelete: "cascade" }).notNull(),
  name: text("name").notNull(),
  slug: text("slug"), // Warframe Market item slug - may be null for items not found
  quantity: integer("quantity").notNull().default(1),
  sellPrices: jsonb("sell_prices").$type<number[]>().default(sql`'[]'::jsonb`),
  buyPrices: jsonb("buy_prices").$type<number[]>().default(sql`'[]'::jsonb`),
  avgSell: integer("avg_sell").default(0), // in platinum * 100 for precision
  avgBuy: integer("avg_buy").default(0), // in platinum * 100 for precision
  marketUrl: text("market_url"),
  source: text("source", { enum: ["screenshot", "excel"] }).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
}, (table) => ({
  sourceCheck: check("source_check", sql`${table.source} IN ('screenshot', 'excel')`),
}));

export const usersRelations = relations(users, ({ many }) => ({
  sessions: many(sessions),
}));

export const sessionsRelations = relations(sessions, ({ one, many }) => ({
  user: one(users, {
    fields: [sessions.userId],
    references: [users.id],
  }),
  items: many(inventoryItems),
}));

export const inventoryItemsRelations = relations(inventoryItems, ({ one }) => ({
  session: one(sessions, {
    fields: [inventoryItems.sessionId],
    references: [sessions.id],
  }),
}));

// Insert schemas
export const insertUserSchema = createInsertSchema(users).pick({
  telegramId: true,
  username: true,
  firstName: true,
  lastName: true,
});

export const insertSessionSchema = createInsertSchema(sessions).pick({
  userId: true,
  telegramId: true,
  type: true,
  status: true,
  photoLimit: true,
  expiresAt: true,
});

export const insertInventoryItemSchema = createInsertSchema(inventoryItems).pick({
  sessionId: true,
  name: true,
  slug: true,
  quantity: true,
  sellPrices: true,
  buyPrices: true,
  avgSell: true,
  avgBuy: true,
  marketUrl: true,
  source: true,
}).extend({
  sellPrices: z.array(z.number()).optional(),
  buyPrices: z.array(z.number()).optional(),
});

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type Session = typeof sessions.$inferSelect;
export type InsertSession = z.infer<typeof insertSessionSchema>;

export type InventoryItem = typeof inventoryItems.$inferSelect;
export type InsertInventoryItem = z.infer<typeof insertInventoryItemSchema>;

// Warframe Market API types
export interface WarframeMarketItem {
  slug: string;
  name: string;
  sellPrices: number[];
  buyPrices: number[];
  avgSell: number;
  avgBuy: number;
  marketUrl: string;
}

