import { type InsertInventoryItem, type InventoryItem } from '@shared/schema';

// ‚Üì‚Üì‚Üì –≠–¢–ê –§–£–ù–ö–¶–ò–Ø –ü–û–õ–ù–û–°–¢–¨–Æ –ó–ê–ú–ï–ù–ï–ù–ê –ù–ê –ù–û–í–£–Æ, –ù–ê–î–ï–ñ–ù–£–Æ –í–ï–†–°–ò–Æ ‚Üì‚Üì‚Üì
export function consolidateItems(newItems: InsertInventoryItem[], existingItems: InventoryItem[]): InsertInventoryItem[] {
  // –ï—Å–ª–∏ –Ω–æ–≤—ã—Ö –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –Ω–µ—Ç, –ø—Ä–æ—Å—Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Å—Ç–∞—Ä—ã–µ, –Ω–∏—á–µ–≥–æ –Ω–µ –º–µ–Ω—è—è
  if (newItems.length === 0) {
    return existingItems as InsertInventoryItem[];
  }

  // –ë–µ—Ä–µ–º sessionId –∏–∑ –ø–µ—Ä–≤–æ–≥–æ –Ω–æ–≤–æ–≥–æ –ø—Ä–µ–¥–º–µ—Ç–∞. –û–Ω –±—É–¥–µ—Ç –æ–¥–∏–Ω–∞–∫–æ–≤—ã–π –¥–ª—è –≤—Å–µ—Ö.
  const currentSessionId = newItems[0].sessionId;
  if (!currentSessionId) {
    // –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –∑–∞—â–∏—Ç–∞: –µ—Å–ª–∏ —É –Ω–æ–≤—ã—Ö –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –Ω–µ—Ç sessionId, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Å—Ç–∞—Ä—ã–µ
    console.error("Critical error: new items are missing sessionId in consolidateItems");
    return existingItems as InsertInventoryItem[];
  }

  const consolidatedMap = new Map<string, InsertInventoryItem>();

  // 1. –°–Ω–∞—á–∞–ª–∞ –¥–æ–±–∞–≤–ª—è–µ–º –≤—Å–µ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –ø—Ä–µ–¥–º–µ—Ç—ã –≤ Map, —á—Ç–æ–±—ã —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –∏—Ö –ø–æ—Ä—è–¥–æ–∫ –∏ –¥–∞–Ω–Ω—ã–µ
  existingItems.forEach(item => {
    const normalizedName = normalizeItemName(item.name);
    consolidatedMap.set(normalizedName, { ...item });
  });

  // 2. –ó–∞—Ç–µ–º –æ–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –Ω–æ–≤—ã–µ –ø—Ä–µ–¥–º–µ—Ç—ã, –æ–±–Ω–æ–≤–ª—è—è –∏–ª–∏ –¥–æ–±–∞–≤–ª—è—è –∏—Ö –≤ Map
  newItems.forEach(newItem => {
    const normalizedName = normalizeItemName(newItem.name);
    const existing = consolidatedMap.get(normalizedName);

    if (existing) {
      // –ï—Å–ª–∏ –ø—Ä–µ–¥–º–µ—Ç —É–∂–µ –µ—Å—Ç—å, –æ–±–Ω–æ–≤–ª—è–µ–º –µ–≥–æ: —Å—É–º–º–∏—Ä—É–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∏ –±–µ—Ä–µ–º —Å–≤–µ–∂–∏–µ –¥–∞–Ω–Ω—ã–µ —Å –º–∞—Ä–∫–µ—Ç–∞
      existing.quantity += newItem.quantity;
      existing.sellPrices = newItem.sellPrices;
      existing.buyPrices = newItem.buyPrices;
      existing.avgSell = newItem.avgSell;
      existing.avgBuy = newItem.avgBuy;
      existing.marketUrl = newItem.marketUrl;
      existing.slug = newItem.slug;
    } else {
      // –ï—Å–ª–∏ –ø—Ä–µ–¥–º–µ—Ç–∞ –Ω–µ—Ç, –ø—Ä–æ—Å—Ç–æ –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –≤ Map
      consolidatedMap.set(normalizedName, newItem);
    }
  });

  // 3. –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º Map –æ–±—Ä–∞—Ç–Ω–æ –≤ –º–∞—Å—Å–∏–≤
  const finalItems = Array.from(consolidatedMap.values());

  // –§–∏–Ω–∞–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞, —á—Ç–æ–±—ã —É –≤—Å–µ—Ö –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –±—ã–ª –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π sessionId
  finalItems.forEach(item => item.sessionId = currentSessionId);

  return finalItems;
}


// --- –≠—Ç–∞ —Ñ—É–Ω–∫—Ü–∏—è –æ—Å—Ç–∞–µ—Ç—Å—è –±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π ---
export function consolidateNewItems(items: InsertInventoryItem[]): InsertInventoryItem[] {
  const consolidated = new Map<string, InsertInventoryItem>();

  items.forEach(item => {
    const normalizedName = normalizeItemName(item.name);
    const existing = consolidated.get(normalizedName);

    if (existing) {
      // Consolidate quantities
      existing.quantity = (existing.quantity || 1) + (item.quantity || 1);
      // Keep the first occurrence's other data
    } else {
      consolidated.set(normalizedName, { ...item });
    }
  });

  return Array.from(consolidated.values());
}


// --- –≠—Ç–∞ —Ñ—É–Ω–∫—Ü–∏—è –æ—Å—Ç–∞–µ—Ç—Å—è –±–µ–∑ –∏–∑–º–µ–Ω–µ–Ω–∏–π ---
function normalizeItemName(name: string): string {
  if (!name) return ''; // –î–æ–±–∞–≤–ª–µ–Ω–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –ø—É—Å—Ç—É—é —Å—Ç—Ä–æ–∫—É –¥–ª—è –Ω–∞–¥–µ–∂–Ω–æ—Å—Ç–∏
  return name
    .toLowerCase()
    .trim()
    .replace(/\s+/g, ' ')
    .replace('—ë', '–µ')
    // Handle common variations (—ç—Ç–∏ –ø—Ä–∞–≤–∏–ª–∞ –º–æ–≥—É—Ç –∫–æ–Ω—Ñ–ª–∏–∫—Ç–æ–≤–∞—Ç—å, –ª—É—á—à–µ –≤—ã–±—Ä–∞—Ç—å –æ–¥–Ω–æ)
    .replace(/\(—á–µ—Ä—Ç–µ–∂\)/gi, '(—á–µ—Ä—Ç—ë–∂)')
    // .replace(/–ø—Ä–∞–π–º/g, 'prime') // –≠—Ç–∏ –ø—Ä–∞–≤–∏–ª–∞ –º–æ–≥—É—Ç –±—ã—Ç—å –æ–ø–∞—Å–Ω—ã, –µ—Å–ª–∏ —É –≤–∞—Å –µ—Å—Ç—å –∏ —Ä—É—Å—Å–∫–∏–µ, –∏ –∞–Ω–≥–ª. –Ω–∞–∑–≤–∞–Ω–∏—è
    // .replace(/—Å–∏—Å—Ç–µ–º–∞/g, 'systems')
}

