const WFM_BASE_URL = "https://api.warframe.market/v2";
const HEADERS = { 'Platform': 'pc', 'Language': 'ru' };

async function fetchAndSaveItems() {
  console.log("–ó–∞–ø—É—Å–∫–∞—é —Å–∫—Ä–∏–ø—Ç –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è —Å–ø–∏—Å–∫–∞ –≤—Å–µ—Ö –ø—Ä–µ–¥–º–µ—Ç–æ–≤ —Å Warframe Market...");

  try {
    const response = await fetch(`${WFM_BASE_URL}/items`, { headers: HEADERS });
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    }

    const data = await response.json();
    const items = data.data || [];

    // –°–æ–∑–¥–∞–µ–º –ø–∞–ø–∫—É 'data', –µ—Å–ª–∏ –µ–µ –Ω–µ—Ç
    const dataDir = path.join(process.cwd(), 'data');
    if (!fs.existsSync(dataDir)) {
      fs.mkdirSync(dataDir);
    }

    // –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ –≤ —Ñ–∞–π–ª
    const filePath = path.join(dataDir, 'items.json');
    fs.writeFileSync(filePath, JSON.stringify(items, null, 2));

    console.log(`‚úÖ –£—Å–ø–µ—à–Ω–æ! –°–ø–∏—Å–æ–∫ –∏–∑ ${items.length} –ø—Ä–µ–¥–º–µ—Ç–æ–≤ —Å–æ—Ö—Ä–∞–Ω–µ–Ω –≤ —Ñ–∞–π–ª: ${filePath}`);

  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –∏–ª–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ —Å–ø–∏—Å–∫–∞ –ø—Ä–µ–¥–º–µ—Ç–æ–≤:', error);
  }
}

fetchAndSaveItems();
